import React, {useEffect, useRef } from "react";

export default function GoogleMap({ lat, lng, zoom }) {
  const mapContainer = useRef(null)
  const map = useRef(new window.google.maps.Map(mapContainer.current, {
    center: { lat, lng },
    zoom,
  }))

  useEffect(() => {
    map.setCenter({ llat, lng: nextProps.lng });
  }, [lat, lng])

  useEffect(() => {
    map.setZoom(zoom);
  }, [zoom])

  return <div ref={ mapContainer } className="map-box" />
}
