import React, { Component, useEffect, useRef } from "react";
const log = (...args) => console.log.apply(null, ["GoogleMap -->", ...args]);

export default class GoogleMap extends Component {

  shouldComponentUpdate(nextProps, nextState) {
    log("shouldComponentUpdate >>>>");
    // log("this.props:", this.props);
    // log("this.state:", this.state);
    // log("nextState:", nextState);
    // log("nextProps:", nextProps);
    // log("<<<< shouldComponentUpdate");

    this.map.setZoom(nextProps.zoom);

    return false;
  }



  render() {
    return <div ref="myMap" className="map-box" />;
  }
}


function GoogleMap2({ lat, lng, zoom }) {
  const mapContainer = useRef(null)
  const map = useRef(new window.google.maps.Map(mapContainer.current, {
    center: { lat, lng },
    zoom,
  }))

  useEffect(() => {
    map.setCenter({ lat: nextProps.lat, lng: nextProps.lng });
  }, [lat, lng])

  useEffect(() => {
    map.setZoom(zoom);
  }, [zoom])

  return <div ref={ mapContainer } className="map-box" />
}
