import React, { Component } from "react";
import TopBar from "./TopBar";
import GoogleMap from "./Google_map";
const { log } = console;

export default class App extends Component {
  input = null;

  state = {
    lat: -34.397,
    lng: 150.644,
    zoom: 8
  };

  // reposition = (event)=> {
  //  const city = event.target.dataset.city
  reposition(city) {
    switch (city) {
      case "tel aviv":
        this.setState({ lat: 32.0042938, lng: 34.7615399 });
        break;
      case "london":
        this.setState({ lat: 51.528308, lng: -0.3817828 });
        break;
      case "paris":
        this.setState({ lat: 48.8587741, lng: 2.2069754 });
        break;
      default:
        alert("wrong city");
    }
  }
  updateZoom = (event) => {
    // const z = event.target.value;
    const z = this.input.value;
    // log({ z });
    // log("typeof z: ", typeof z);
    this.setState({ zoom: Number(z) }); // parseInt(zoom); // +zoom;
    // this.setState({ zoom: parseInt(z,10) }); // parseInt(zoom); // +zoom;
    // this.setState({ zoom: +z }); // parseInt(zoom); // +zoom;
  };
  render() {
    log(this.state);
    return (
      <div className="app">
        <TopBar>Google Maps Example in React</TopBar>
        <div className="hbox mb20">
          <button
            data-city="london"
            onClick={() => this.reposition("tel aviv")}
          >
            Tel Aviv
          </button>
          <button data-city="paris" onClick={() => this.reposition("paris")}>
            Paris
          </button>
          <button onClick={this.reposition.bind(this, "london")}>London</button>
          <input
            ref={(elm) => (this.input = elm)}
            type="number"
            min="8"
            max="16"
            placeholder="8"
            onChange={this.updateZoom}
          />
        </div>
        <GoogleMap {...this.state} />
        {/* <GoogleMap
          lat={this.state.lat}
          lng={this.state.lng}
          zoom={this.state.zoom}
        />  */}
      </div>
    );
  }
}
